import React, { useState } from "react";

const ImageHighlighter = ({ selectedPiece }: selectedPieceProps) => {

  const baseImage = `/images/default-${area}.png`;
  const highlightImages = {
    abdomen: {
      umbilicus: "/images/umbilicus-highlight.png",
      suprapubic: "/images/suprapubic-highlight.png",
      ruq: "/images/ruq-highlight.png",
    },
    finger: {},
  };
  const labels = {
    abdomen: {
      umbilicus: "/images/umbilicus-active.png",
      suprapubic: "/images/suprapubic-active.png",
      ruq: "/images/ruq-active.png",
    },
    finger: {},
  };
  return (
    <div className="relative">
      <img
        className="justify-center items-center"
        src={baseImage}
        alt="default-abs"
      />
      <ImageHighlighter slectedPiece={selectedAbdomen} />
      {selectedAbdomen.umbilicus && (
        <div>
          <img
            src={highlightImages[area].umbilicus}
            alt="Umbilicus highlighted"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
          <img
            src={labels[area].umbilicus}
            alt="Umbilicus label"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
        </div>
      )}
      {selectedAbdomen.suprapubic && (
        <div>
          <img
            src={highlightImages[area].suprapubic}
            alt="suprapubic highlighted"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
          <img
            src={labels[area].suprapubic}
            alt="suprapubic label"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
        </div>
      )}
      {selectedAbdomen.ruq && (
        <div>
          <img
            src={highlightImages[area].ruq}
            alt="ruq highlighted"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
          <img
            src={labels[area].ruq}
            alt="ruq label"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
        </div>
      )}
      <div
        className="absolute top-[49%]	left-[43.5%] w-20 h-20 cursor-pointer rounded-full"
        onClick={() => toggleArea("umbilicus")}
        style={{ border: "1px solid red", opacity: 1 }}
      ></div>
      <div
        className="absolute top-[59%]	left-[42.5%] w-24 h-24 cursor-pointer rounded-full"
        onClick={() => toggleArea("suprapubic")}
        style={{ border: "1px solid red", opacity: 1 }}
      ></div>
      <div
        className="absolute top-[42.5%]	left-[33%] w-24 h-24 cursor-pointer rounded-full"
        onClick={() => toggleArea("ruq")}
        style={{ border: "1px solid red", opacity: 1 }}
      ></div>
    </div>
  );
};
export default ImageHighlighter;
