import React, { useState } from 'react';
const AreaSelector = ({ area}) => {
    interface AbdomenState {
        ruq: boolean;
        riq: boolean;
        umbilicus: boolean;
      }
      const [selectedAbdomen, setSelectedAbdomen] = useState<AbdomenState>({
        ruq: false,
        riq: false,
        umbilicus: false,
      });

    const toggleArea = (area: keyof AbdomenState) => {
        setSelectedAbdomen((prevState) => ({
          ...prevState,
          [area]: !prevState[area], // Toggle the selected area
        }));
        console.log("selectedabdomen", selectedAbdomen);
      };
    const baseImage = `/images/default-${area}.png`;
    const highlightImages = {
        abdomen: {
          umbilicus: "/images/umbilicus-highlight.png",
        },
        finger: {
          dip: '/images/dip-highlight.png',
          liq: '/images/liq-highlight.png',
        }
    };
    const labels = {
        abdomen: {
            umbilicus: "/images/umbilicus-active.png",
        },
        finger: {

        }
      };
    return (
        <div className="relative">
          <img
            className="justify-center items-center"
            src="/images/default-abs.png"
            alt="default-abs"
          />
          {selectedAbdomen.umbilicus && (
            <div>
              <img
                src={highlightImages.area.umbilicus}
                alt="Umbilicus highlighted"
                className="absolute"
                style={{
                  top: "0",
                  left: "0",
                }}
              />
              <img
                src={labels.area.umbilicus}
                alt="Umbilicus label"
                className="absolute"
                style={{
                  top: "0",
                  left: "0",
                }}
              />
            </div>
          )}
          <div
            className="absolute top-[49%]	left-[43.5%] w-20 h-20 cursor-pointer rounded-full"
            onClick={() => toggleArea("umbilicus")}
            style={{ border: "1px solid red", opacity: 1 }}
          ></div>
        </div>
    );
};
export default AreaSelector;
