import React from "react";
import { FingerState } from "../../../src/types";

interface FingerSelectorProps {
  selectedFinger: FingerState;
  setSelectedFinger: React.Dispatch<React.SetStateAction<FingerState>>;
}
const FingerSelector: React.FC<FingerSelectorProps> = ({
  selectedFinger,
  setSelectedFinger,
}) => {
  const toggleArea = (area: keyof FingerState) => {
    if (area === "selectAll") {
      setSelectedFinger({
        mcp: true,
        dip: true,
        pip: true,
        selectAll: true,
      });
    } else {
      setSelectedFinger((prevState) => ({
        ...prevState,
        [area]: !prevState[area], // Toggle the selected area
        selectAll: false,
      }));
    }
  };
  const baseImage = "/images/default-finger.png";
  const fingerAreas = ["mcp", "dip", "pip"];
  const highlightImages: {
    finger: { [key: string]: string };
  } = {
    finger: {},
  };
  const labels: {
    finger: { [key: string]: string };
  } = {
    finger: {},
  };
  fingerAreas.forEach((fingerArea) => {
    highlightImages.finger[fingerArea] =
      fingerArea === "selectAll"
        ? "/images/others-highlight.png"
        : `/images/${fingerArea}-highlight.png`;
    labels.finger[fingerArea] = selectedFinger.selectAll
      ? ""
      : `/images/${fingerArea}-active.png`;
  });
  return (
    <div className="relative">
      <img
        className="justify-center items-center"
        src={baseImage}
        alt="default-finger"
      />
      {selectedFinger.mcp && (
        <div>
          <img
            src={highlightImages["finger"].mcp}
            alt="MCP highlighted"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
          <img
            src={labels["finger"].mcp}
            alt="MCP label"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
        </div>
      )}
      {selectedFinger.dip && (
        <div>
          <img
            src={highlightImages["finger"].dip}
            alt="DIP highlighted"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
          <img
            src={labels["finger"].dip}
            alt="DIP label"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
        </div>
      )}
      {selectedFinger.pip && (
        <div>
          <img
            src={highlightImages["finger"].pip}
            alt="PIP highlighted"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
          <img
            src={labels["finger"].pip}
            alt="PIP label"
            className="absolute"
            style={{
              top: "0",
              left: "0",
            }}
          />
        </div>
      )}
      {Object.keys(selectedFinger).map((fingerArea) => (
        <HighlightedArea
          key={fingerArea}
          isSelected={selectedAbdomen[fingerArea as keyof FingerState]}
          highlightImage={highlightImages.finger[fingerArea]}
          labelImage={labels.finger?.[fingerArea]}
          position={{ top: "0", left: "0" }} // Customize the position as needed
          altText={selectedFinger.selectAll ? "" : abdomenArea}
        />
      ))}
      <div
        className="absolute top-[49%]	left-[43.5%] w-20 h-20 cursor-pointer rounded-full"
        onClick={() => toggleArea("mcp")}
        style={{ border: "1px solid red", opacity: 0 }}
      ></div>
    </div>
  );
};
export default FingerSelector;
