import React from "react";

type HighlightedAreaProps = {
  isSelected: boolean;
  highlightImage: string;
  labelImage: string;
  position: {
    top: string;
    left: string;
    labelTop?: string;
    labelLeft?: string;
  };
  altText: string;
};

const HighlightedArea = ({
  isSelected,
  highlightImage,
  labelImage,
  position,
  altText,
}: HighlightedAreaProps) => {
  if (!isSelected) return null;

//   const customLabelTop =
//     altText === "ruq" ? "70px" : position.labelTop || position.top;

  let customLabelTop = position.labelTop || position.top;
  if (altText === "ruq") {
    customLabelTop = "70px";
  } else if (altText === "rlq") {
    customLabelTop = "60px";
  }
  return (
    <>
      <img
        src={highlightImage}
        alt={`${altText} highlighted`}
        className="absolute"
        style={{ top: position.top, left: position.left }}
      />
      <img
        src={labelImage}
        alt={`${altText} label`}
        className="absolute"
        style={{
          top: customLabelTop,
          left: position.labelLeft || position.left,
        }}
      />
    </>
  );
};

export default HighlightedArea;
