import React, { useState } from "react";
import '../src/components/'

const App: React.FC = () => {
  interface AbdomenState {
    ruq: boolean;
    riq: boolean;
    umbilicus: boolean;
  }
  const [selectedAbdomen, setSelectedAbdomen] = useState<AbdomenState>({
    ruq: false,
    riq: false,
    umbilicus: false,
  });
  const toggleArea = (area: keyof AbdomenState) => {
    setSelectedAbdomen((prevState) => ({
      ...prevState,
      [area]: !prevState[area], // Toggle the selected area
    }));
    console.log("selectedabdomen", selectedAbdomen);
  };
  return (
    <div className="flex-row justify-center h-screen bg-gray-100 px-4">
      <div className="flex items-center">
        <img className="w-20" src="/agnos-logo.png" alt="agnos-logo" />
        <h2 className="text-2xl font-bold py-8 leading-7 text-gray-900 sm:truncate sm:text-3xl sm:tracking-tight">
          Welcome to Agnos Health Check
        </h2>
      </div>
      <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
        <h3 className="text-3xl font-bold mb-6">Select Pain Areas</h3>
         {/* Abdomen */}
        <div className="mb-6">
          <h2 className="text-xl font-semibold">Abdomen</h2>
          <AreaSelector
            area="abdomen"
            isSelected={selectedAbdomen}
            toggleArea={area => toggleArea('abdomen', area)}
          />
        </div>
      </div>
    </div>
  );
};

export default App;
