import React, {useState} from "react";

const App: React.FC = () => {
  const highlightImages = {
    umbilicus: "/images/umbilicus-highlight.png",
  };
  const [selectedAbdomen, setSelectedAbdomen] = useState({ umbilicus: false, riq: false });

  interface AbdomenState {
    ruq: boolean;
    riq: boolean;
    umbilicus: boolean;
  }
  const toggleArea = (area: string) => {
    setSelectedAbdomen((prevState: AbdomenState) => ({
      ...prevState,
      [area]: !prevState[area], // Toggle the selected area
    }));
  };
  return (
    <div className="flex-row justify-center h-screen bg-gray-100 px-4">
      <div className="flex items-center">
        <img className="w-20" src="/agnos-logo.png" alt="agnos-logo" />
        <h2 className="text-2xl font-bold py-8 leading-7 text-gray-900 sm:truncate sm:text-3xl sm:tracking-tight">
          Welcome to Agnos Health Check
        </h2>
      </div>
      <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center">
        <h3 className="text-3xl font-bold mb-6">Select Pain Areas</h3>

        <div className="relative">
          <img
            className="justify-center items-center"
            src="/images/default-abs.png"
            alt="default-abs"
          />
          <img
            src={highlightImages.umbilicus}
            alt="Umbilicus highlighted"
            className="absolute"
            style={{ top: "0", left: "0" }} // Adjust this position as needed
          />
          <div
            className="absolute top-0 left-0 w-1/2 h-1/2 cursor-pointer"
            onClick={() => toggleArea("umbilicus")}
            style={{ border: "1px solid red", opacity: 0 }}
          ></div>
        </div>
      </div>
    </div>
  );
};

export default App;
